from tkinter import font
import PySimpleGUI as sg

## imagens
image_large_rectangle = b''

sg.theme('BlueMono')
layout = [[sg.Text('Full Scan           ',font='Default 16'),sg.Button('Iniciar Verificação', image_data=image_large_rectangle, font='Helvetica 12 bold italic', button_color=('white', sg.theme_background_color()),
                      border_width=0, )],
         [sg.Text('Verificar Arquivo',font='Default 16'),sg.Button('Selecionar Arquivo', image_data=image_large_rectangle, font='Helvetica 12 bold italic', button_color=('white', sg.theme_background_color()),
                      border_width=0, )],
         [sg.Button('Tarefas', image_data=image_large_rectangle, font='Helvetica 12 bold italic', button_color=('white', sg.theme_background_color()),
                      border_width=0, ),sg.Button('Configurações', image_data=image_large_rectangle, font='Helvetica 12 bold italic', button_color=('white', sg.theme_background_color()),
                      border_width=0, ),],
         
]

window = sg.Window('AllSafe Antivirus',layout)

while True:
    event, Values = window.read()
    if event == sg.WIN_CLOSED:
        break
window.close()